import React from "react";
import { Dropdown, Form, Button, Header } from "semantic-ui-react";

const options = [
  { key: "1", text: "$", value: "1" },
  { key: "2", text: "$$", value: "2" },
  { key: "3", text: "$$$", value: "3" },
  { key: "4", text: "$$$$", value: "4" }
];

class Price extends React.Component {
  saveAndContinue = e => {
    console.log(this.props.values);
    e.preventDefault();
    this.props.nextStep();
  };

  back = e => {
    e.preventDefault();
    this.props.prevStep();
  };

  render() {
    const { values } = this.props;
    return (
      <Grid
        verticalAlign="middle"
        centered
        style={{ height: "25vh", marginTop: "25vh" }}
      >
        <Card>
          <Card.Header as="h1" textAlign="center">
            Enter User Details
          </Card.Header>
          <Card.Content>
            <Form color="green">
              <Dropdown
                style={{ marginTop: "25%" }}
                onChange={this.props.handleChange("radius")}
                label="Restaurant Rating - Minimum"
                selection
                fluid
                search
                options={options}
                values={values}
                placeholder="Minimum Rating"
              />
              <Dropdown
                style={{ marginTop: "10px" }}
                onChange={this.props.handleChange("rating_max")}
                label="Restaurant Rating - Maximum"
                selection
                fluid
                search
                options={options}
                values={values}
                placeholder="Maximum Rating"
              />
              <Button onClick={this.back}>Back</Button>
              <Button
                onClick={this.saveAndContinue}
                style={{ marginTop: "10px" }}
              >
                Save And Continue{" "}
              </Button>
            </Form>
          </Card.Content>
        </Card>
      </Grid>
    );
  }
}

export default Price;
