import React from "react";

import Unsplash from "../api/cloud";

import Type from "../components/type";
import Radius from "../components/radius";

import "../css/App.css";

class Home extends React.Component {
  state = {
    step: 1,
    type: "",
    radius: "",
    price: "",
    rating: ""
  };

  nextStep = () => {
    const { step } = this.state;
    this.setState({
      ...this.state,
      step: step + 1
    });
  };

  prevStep = () => {
    const { step } = this.state;
    this.setState({
      ...this.state,
      step: step - 1
    });
  };

  handleChange = input => event => {
    this.setState({ [input]: event.target.value });
  };

  render() {
    const { step } = this.state;
    const { type, radius, price, rating } = this.state;
    const values = { type, radius, price, rating };
    switch (step) {
      case 1:
        return (
          <Type
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 2:
        return (
          <PersonalDetails
            nextStep={this.nextStep}
            prevStep={this.prevStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 3:
        return (
          <Confirmation
            nextStep={this.nextStep}
            prevStep={this.prevStep}
            values={values}
          />
        );
      case 4:
        return <Success />;
    }
  }
}

export default Home;
