import React from "react";
import { Dropdown, Form, Button } from "semantic-ui-react";

const options = [
  { key: "1 Star", text: "1 Star", value: "1" },
  { key: "2 Star", text: "2 Star", value: "2" },
  { key: "3 Star", text: "3 Star", value: "3" },
  { key: "4 Star", text: "4 Star", value: "4" },
  { key: "5 Star", text: "5 Star", value: "5" }
];

class Rating extends React.Component {
  saveAndContinue = e => {
    e.preventDefault();
    this.props.nextStep();
  };

  back = e => {
    e.preventDefault();
    this.props.prevStep();
  };

  render() {
    const { values } = this.props;
    return (
      <Form color="green">
        <h1 className="ui centered">Enter User Details</h1>
        <Dropdown
          onChange={this.props.handleChange("rating_min")}
          label="Restaurant Rating - Minimum"
          selection
          fluid
          search
          options={options}
          values={values}
          placeholder="X Stars"
        />
        <Dropdown
          onChange={this.props.handleChange("rating_max")}
          label="Restaurant Rating - Maximum"
          selection
          fluid
          search
          options={options}
          values={values}
          placeholder="X Stars"
        />

        <Button onClick={this.back}>Back</Button>
        <Button onClick={this.saveAndContinue}>Save And Continue </Button>
      </Form>
    );
  }
}

export default Rating;
