import React from "react";
import { Dropdown, Form, Button, Header } from "semantic-ui-react";

const options = [
  { key: "1", text: "1 Mile", value: "1" },
  { key: "2", text: "2 Miles", value: "2" },
  { key: "3", text: "3 Miles", value: "3" },
  { key: "4", text: "4 Miles", value: "4" },
  { key: "5", text: "5 Miles", value: "5" },
  { key: "10", text: "10 Miles", value: "10" },
  { key: "15", text: "15 Miles", value: "15" },
  { key: "20", text: "20 Miles", value: "20" }
];

class Radius extends React.Component {
  saveAndContinue = e => {
    console.log(this.props.values);
    e.preventDefault();
    this.props.nextStep();
  };

  back = e => {
    e.preventDefault();
    this.props.prevStep();
  };

  render() {
    const { values } = this.props;
    return (
      <Grid
        verticalAlign="middle"
        centered
        style={{ height: "25vh", marginTop: "25vh" }}
      >
        <Card>
          <Card.Header as="h1" textAlign="center">
            Enter User Details
          </Card.Header>
          <Card.Content>
            <Form color="green">
              <Dropdown
                style={{ marginTop: "25%" }}
                onChange={this.props.handleChange("radius")}
                label="Radius to Food"
                selection
                fluid
                search
                options={options}
                values={values}
                placeholder="Radius in Miles"
              />

              <Button
                onClick={this.saveAndContinue}
                style={{ marginTop: "10px" }}
              >
                Save And Continue{" "}
              </Button>
            </Form>
          </Card.Content>
        </Card>
      </Grid>
    );
  }
}

export default Radius;
